import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Article.css";

const ArticleList = ({ token }) => {
  const [posts, setPosts] = useState([]);
  const admin = token.admin;
  useEffect(() => {
    fetchAPI();
  }, []);

  async function fetchAPI() {
    const response = await fetch(`/api/posts`, {
      method: "get",
      headers: {
        Authorization: `Bearer ${token.token}`,
      },
    });
    const body = await response.json();
    console.log(body);
    setPosts(body);
  }
  const confirmDelete = (event) => {
    const confirm = window.confirm("Delete the blog post?");
    if (!confirm) event.preventDefault();
  };
  return (
    <div>
      <h1 id="article">Article List</h1>
      <Link className={admin ? "createBlog" : "hidden"} to="/post/create">
        Create Post
      </Link>
      {posts.map((i, key) => (
        <div className="articleList" key={key}>
          <Link className="articleListItem" to={`/article/${i._id}`}>
            {i.title}
          </Link>
          <Link
            className={admin ? "articleListItem" : "hidden"}
            to={`/post/update/${i._id}`}
          >
            ‚úç
          </Link>
          <Link
            onClick={confirmDelete}
            className={admin ? "articleListItem icon" : "hidden"}
            to={`/post/delete/${i._id}`}
          >
            üóë
          </Link>
        </div>
      ))}
    </div>
  );
};

export default ArticleList;
